const body = $json.body || $json;

// Captura os dados
const user_id = body.user_id;

// aceita vários nomes
const rawDate =
  body.datetime_iso ||
  body.datetime_text ||
  body.datetime_hint;

const titleRaw = body.title;
const descriptionRaw = body.description;

if (!user_id || !rawDate) {
  throw new Error("user_id e data são obrigatórios");
}

let finalDateISO;

function nextWeekdayAt(hour, minute, weekdayIndex, tzOffset = "-03:00") {
  // weekdayIndex: 0=domingo ... 6=sábado
  const now = new Date();
  const d = new Date(now);
  d.setSeconds(0, 0);

  const today = d.getDay();
  let delta = (weekdayIndex - today + 7) % 7;

  if (delta === 0) {
    // se for hoje e já passou da hora, joga pra próxima semana
    const nowMinutes = d.getHours() * 60 + d.getMinutes();
    const targetMinutes = hour * 60 + minute;
    if (targetMinutes <= nowMinutes) delta = 7;
  }

  d.setDate(d.getDate() + delta);
  d.setHours(hour, minute, 0, 0);

  const yyyy = d.getFullYear();
  const mm = String(d.getMonth() + 1).padStart(2, "0");
  const dd = String(d.getDate()).padStart(2, "0");
  const HH = String(d.getHours()).padStart(2, "0");
  const MM = String(d.getMinutes()).padStart(2, "0");

  return `${yyyy}-${mm}-${dd}T${HH}:${MM}:00${tzOffset}`;
}

function parseHint(raw) {
  const s = String(raw).toLowerCase().trim();

  // hora: "15h", "15h30", "15:30"
  const m =
    s.match(/(\d{1,2})\s*h\s*(\d{1,2})?/) ||
    s.match(/(\d{1,2})\s*:\s*(\d{1,2})/);

  if (!m) return null;

  const hour = Number(m[1]);
  const minute = Number(m[2] ?? 0);
  if (hour < 0 || hour > 23 || minute < 0 || minute > 59) return null;

  const weekdays = {
    "domingo": 0,
    "segunda": 1,
    "segunda-feira": 1,
    "terca": 2,
    "terça": 2,
    "terça-feira": 2,
    "quarta": 3,
    "quarta-feira": 3,
    "quinta": 4,
    "quinta-feira": 4,
    "sexta": 5,
    "sexta-feira": 5,
    "sabado": 6,
    "sábado": 6,
    "sábado-feira": 6,
  };

  let wd = null;
  for (const key of Object.keys(weekdays)) {
    if (s.includes(key)) {
      wd = weekdays[key];
      break;
    }
  }

  if (wd === null) return null;

  return nextWeekdayAt(hour, minute, wd);
}

// 1) "dd/mm/aaaa hh:mm"
if (typeof rawDate === "string" && rawDate.includes("/")) {
  const [datePart, timePart] = rawDate.split(" ");
  const [day, month, year] = datePart.split("/");
  const time = (timePart || "00:00").trim();
  finalDateISO = `${year}-${month}-${day}T${time}:00-03:00`;
} else {
  // 2) tenta hint tipo "quarta às 15h"
  const hinted = parseHint(rawDate);
  if (hinted) {
    finalDateISO = hinted;
  } else {
    // 3) tenta ISO / formatos parseáveis pelo JS
    const dateObj = new Date(rawDate);
    if (isNaN(dateObj.getTime())) {
      throw new Error("Formato de data inválido");
    }
    finalDateISO = dateObj.toISOString();
  }
}

const title =
  typeof titleRaw === "string" && titleRaw.trim()
    ? titleRaw.trim()
    : "Conversa";

const description =
  typeof descriptionRaw === "string" && descriptionRaw.trim()
    ? descriptionRaw.trim()
    : null;

return [
  {
    user_id,
    scheduled_for: finalDateISO,
    title,
    description,
  },
];
